import React from 'react';
import { render, screen } from '@testing-library/react';
import { composeStories } from '@storybook/testing-react';
import * as stories from './HelpTool.stories'; // import all stories from the stories file

const { Default: HelpTool } = composeStories(stories);

it('renders help tooltip', async () => {
  render(<HelpTool />);
  const helpTool = screen.getByTestId('help-tool');
  expect(helpTool).toBeInTheDocument();
  expect(helpTool).toHaveClass('small ml-2 ml-lg-0 text-primary');
});

it.todo('checks tooltip when open has border radius of 10px');
