import React from 'react';
import '../Checklist.module.scss';

const ChecklistItem = ({ t, item, onChange, isChecked = false }) => (
  <div className="checklist__form-check row">
    <div className="checklist__custom-control col-12 custom-control custom-checkbox">
      <input
        type="checkbox"
        id={item.value}
        onClick={onChange}
        checked={isChecked}
        className="checklist__form-check-input custom-control-input"
      />
      <label
        className="checklist__form-check-label custom-control-label"
        htmlFor={item.value}
      >
        {t(item.label)}
      </label>
    </div>
  </div>
);
export default ChecklistItem;
