import React from 'react';
import { render, screen } from '@testing-library/react';
import { Default } from './MarketingConsent.stories';
import { MarketingConsentProps } from './MarketingConsent';

it('renders the Marketing consent in the default state', () => {
  render(<Default {...(Default.args as MarketingConsentProps)} />);

  expect(screen.getByTestId('tc-option-y')).toBeChecked();

  expect(screen.getByText('HTML with text on top')).toBeInTheDocument();
  expect(screen.getByText('HTML with text on bottom')).toBeInTheDocument();

  expect(screen.getByText('Submit')).toBeInTheDocument();
});
