import React from 'react';
import { render, screen, waitFor } from '@testing-library/react';
import { composeStories } from '@storybook/testing-react';
import userEvent from '@testing-library/user-event';
import * as stories from './MultiSelectField.stories'; // import all stories from the stories file

const { Default } = composeStories(stories);

it('renders the MultiSelectField in the default state', async () => {
  render(<Default />);
  const card = screen.getByTestId('1');
  const selectedCard = screen.getByTestId('3');
  const nextButton = screen.getByText('common:buttons.next');

  expect(card).toBeVisible();
  expect(selectedCard).toBeVisible();
  expect(selectedCard).toHaveClass('multi-select__selected-item');
  userEvent.click(card);
  expect(nextButton).toBeInTheDocument();
  userEvent.click(nextButton);
  await waitFor(() => {
    expect(nextButton).not.toBeVisible();
  });
});
