import React from 'react';
import { render, screen } from '@testing-library/react';
import { composeStories } from '@storybook/testing-react';
import * as stories from './MultiTextFields.stories'; // import all stories from the stories file

const { PhoneAndEmailFieldsView } = composeStories(stories);

it('render phone and email fields inside a multi question', async () => {
  render(<PhoneAndEmailFieldsView />);
  const phone = screen.getByTestId('phone-input');
  const email = screen.getByTestId('email-input');

  expect(phone).toBeInTheDocument();
  expect(email).toBeInTheDocument();
});
