import React from 'react';
import { action } from '@storybook/addon-actions';
import NextButton from './NextButton';

export default {
  component: NextButton,
  title: 'components/NextButton',
};

export const enabled = () => <NextButton t={() => null} isVisible />;
export const disabled = () => (
  <NextButton t={() => null} isDisabled isVisible />
);
export const customText = () => (
  <NextButton t={() => null} isVisible>
    This is Custom
  </NextButton>
);
export const onClick = () => (
  <NextButton t={() => null} isVisible onClick={action('Clicked!')}>
    Click for Action
  </NextButton>
);
