import React from 'react';
import { render, screen, waitFor } from '@testing-library/react';
import { composeStories } from '@storybook/testing-react';
import userEvent from '@testing-library/user-event';
import * as stories from './SquareCheckboxes.stories'; // import all stories from the stories file

const { Default } = composeStories(stories);

it('renders the SquareCheckboxes in the default state', async () => {
  render(<Default />);
  const checkbox1 = screen.getByTestId('1');
  const checkbox2 = screen.getByTestId('2');
  const checkbox3 = screen.getByTestId('3');
  const nextButton = screen.getByText('common:buttons.next');

  expect(checkbox1).toBeVisible();
  userEvent.click(checkbox1);
  await waitFor(() => {
    expect(checkbox1).toBeChecked();
  });

  expect(checkbox2).toBeVisible();
  userEvent.click(checkbox2);

  await waitFor(() => {
    expect(checkbox2).toBeChecked();
  });

  expect(checkbox3).toBeVisible();
  expect(checkbox3).toBeDisabled();

  userEvent.click(checkbox3);
  await waitFor(() => {
    expect(checkbox3).toBeDisabled();
  });

  expect(nextButton).toBeInTheDocument();
  userEvent.click(nextButton);
  await waitFor(() => {
    expect(nextButton).not.toBeVisible();
  });
});
