import TextFieldLayout from './TextFieldLayout';

const TextArea = ({
  t,
  formik,
  handleNextButton,
  question,
  isConfirmButton,
}) => (
  <TextFieldLayout
    t={t}
    tip={question.tipText}
    label={t(question.label)}
    questionCode={question.code}
    handleNextButton={handleNextButton}
    formik={formik}
    isConfirmButton={isConfirmButton}
  >
    {(handleKeyDown) => (
      <textarea
        id="textArea"
        rows="4"
        className={`
                 form-control
                 ${
                   formik.touched[question.code] &&
                   (!formik.errors[question.code] ? 'is-valid' : 'is-invalid')
                 }
               `}
        aria-describedby="textAreaHelp"
        placeholder={t('common:placeholders.textArea')}
        onKeyDown={handleKeyDown}
        {...formik.getFieldProps(question.code)}
      />
    )}
  </TextFieldLayout>
);

export default TextArea;
